import React, { Component } from 'react'
import {Menu,Segment,Container,Header, Button, Image} from 'semantic-ui-react'
import logo from './logo.png'
export default class Menubar extends Component{
    state = { activeItem: 'home' }

    handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
      const { activeItem } = this.state

      return (
          <Menu inverted fixed >
          <Menu.Menu position='left'>
            <Menu.Item id='logo'>
              <Image src={logo} ></Image>
            </Menu.Item>
            <Menu.Item
              name='home'
              active={activeItem === 'home'}
              onClick={this.handleItemClick}
            />

            <Menu.Item
              name='projects'
              active={activeItem === 'projects'}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              name='guides'
              active={activeItem === 'guides'}
              onClick={this.handleItemClick}
            />
            <Menu.Item
              name='contact'
              active={activeItem === 'contact'}
              onClick={this.handleItemClick}
            />
          </Menu.Menu>

            <Menu.Menu position='right'>
              <Menu.Item>        
                <Button name='login' onClick={this.handleItemClick} active={activeItem === 'contact'} > Login</Button>
                <span class='divider'> </span>
                <Button primary name='logout' onClick={this.handleItemClick} active={activeItem === 'contact'}> SignUp</Button>
              </Menu.Item>
            </Menu.Menu>
          </Menu>



      );
    }
}
