import React, { Component } from "react";
import {
  Input,
  Select,
  Item,
  Button,
  Icon,
  Message,
  Label,
  Form,
  Segment
} from "semantic-ui-react";
import axios from "axios";

class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pattern: "",
      projectType: "",
      language: "",
      languageOptions: [
        { text: "All", value: "All" },
        { text: "PHP", value: "PHP" },
        { text: "HTML", value: "HTML" },
        { text: "JAVA", value: "JAVA" }
      ],
      projects: []
    };
  }

  componentDidMount() {
    let data = {};
    if (this.state.pattern !== "" && this.state.pattern !== "All")
      data["pattern"] = this.state.pattern;
    if (this.state.language !== "" && this.state.language !== "ALL")
      data["language"] = this.state.language;
    if (this.state.projectType !== "" && this.state.projectType !== "ALL")
      data["projectType"] = this.state.projectType;

    axios({
      method: "get",
      url: "http://localhost:8080/project",
      params: data
    })
      .then(response => {
        this.setState({ projects: response.data });
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  projectTypeOnChange = (event, data) => {
    switch (data.value) {
      case "Website":
        this.setState({
          languageOptions: [
            { text: "All", value: "All" },
            { text: "PHP", value: "PHP" },
            { text: "HTML", value: "HTML" }
          ]
        });
        break;
      case "Window Application":
        this.setState({
          languageOptions: [
            { text: "All", value: "All" },
            { text: "JAVA", value: "JAVA" }
          ]
        });
        break;
      case "Android Appliation":
        this.setState({
          languageOptions: [{ text: "All", value: "All" }]
        });
        break;
      default:
    }
  };

  searchOnChange = e => {
    this.setState({ pattern: e.target.value });
    console.log(e.target.value);
  };

  searchOnClick = e => {
    console.log("clicked .");
  };

  filterDate = date => {
    var today = new Date();
    var diff = Math.floor(
      (Date.UTC(
        today.getFullYear(),
        today.getMonth(),
        today.getDate(),
        today.getHours(),
        today.getMinutes()
      ) -
        Date.UTC(
          date.getFullYear(),
          date.getMonth(),
          date.getDate(),
          date.getHours(),
          date.getMinutes()
        )) /
        (1000 * 60 * 60 * 24)
    );
    switch (diff) {
      case diff == 0:
        return "1 day ago";
    }
    console.log("diff :", diff);
  };
  render() {
    console.log("projects:", this.state.projects);
    const items = this.state.projects.map(project => {
      console.log(this.filterDate(new Date(project.createdDate)));

      return (
        <Item key={project.ProjectName}>
          <Item.Content>
            <Item.Header as="a">{project.ProjectName}</Item.Header>

            <Item.Meta>
              <Label>{}</Label>
              <span> by {project.username}</span>
            </Item.Meta>
            <Item.Description>{project.description}</Item.Description>
            <Item.Extra>
              <Button floated="right" icon="play" color="twitter"></Button>

              <Button floated="right" icon="download" color="twitter" />

              <Label>{project.ProjectType}</Label>

              <Label>{project.Language}</Label>
            </Item.Extra>
          </Item.Content>
        </Item>
      );
    });
    const projectTypeOptions = [
      { text: "All", value: "All" },
      { text: "Website", value: "Website" },
      { text: "Window Application", value: "Window Application" },
      { text: "Android", value: "Android" }
    ];

    return (
      <React.Fragment>
        <div className="projectContent">
          <Form.Group>
            <Segment clearing>
              <Input placeholder="Search..." onChange={this.searchOnChange} />
              <Button content="Search" onClick={this.searchOnClick} />

              <span className="padd"></span>
              <Select
                placeholder="Project Type"
                options={projectTypeOptions}
                onChange={this.projectTypeOnChange}
              />
              <span className="padd"></span>
              <Select
                placeholder="Language"
                options={this.state.languageOptions}
              />
            </Segment>
          </Form.Group>
          <Segment>
            <Item.Group divided>{items}</Item.Group>
          </Segment>
        </div>
      </React.Fragment>
    );
  }
}

export default Projects;
