import React, { Component } from "react";
import {
  Checkbox,
  Button,
  Form,
  Select,
  Input,
  TextArea,
  Divider
} from "semantic-ui-react";

class UploadForm extends Component {
  state = {};

  render() {
    const options = [
      { text: "Website", value: "website" },
      { text: "Window Application", value: "window application" },
      { text: "Android Application", value: "android application" }
    ];
    return (
      <React.Fragment>
        <Form.Field>
          <label> Upload Project Files</label>
          <input directory="" webkitdirectory="" type="file" />
        </Form.Field>
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            options={options}
            label="Language"
            placeholder="title"
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            options={options}
            label="Version"
            placeholder="title"
            width={4}
          />
        </Form.Group>
        <Form.Field control={Checkbox} label="check to upload database" />
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            label="Database"
            placeholder="title"
            options={options}
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            label="Version"
            placeholder="title"
            options={options}
            width={4}
          />
        </Form.Group>
        <Form.Field
          control="input"
          label="Database backup file"
          type="file"
          accept=".sql"
        />
        <Form.Field
          control={TextArea}
          label="About"
          placeholder="Tell us more about you..."
        />
        <Form.Field control={Checkbox} label="allow download" />
        <Button type="submit">Submit</Button>
      </React.Fragment>
    );
  }
}

export default UploadForm;
