import React, { Component } from "react";
import {
  Checkbox,
  Button,
  Form,
  Select,
  Input,
  TextArea,
  Divider
} from "semantic-ui-react";

class WebsiteUploadForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedLanguage: null
    };
  }

  languageOnChange = (event, data) => {
    this.setState({ selectedLanguage: data.value });
  };

  render() {
    const detail = this.props.projectDetail;

    //extract website information
    const webDetail = detail.projects.filter(project => {
      return project.projectType === "Website";
    });
    //extract supported version from webInfo
    console.log(webDetail);
    const version = webDetail[0].language.filter(language => {
      return language.name === this.state.selectedLanguage;
    });
    let versionOption = null;
    if (version.length > 0) {
      versionOption = version[0].version.map(v => {
        return { text: v, value: v };
      });
    }

    //extract supported Language from webInfo
    const languageOption = webDetail[0].language.map(language => {
      return { text: language.name, value: language.name };
    });

    return (
      <React.Fragment>
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            options={languageOption}
            label="language"
            placeholder="Language"
            onChange={this.languageOnChange}
          />
          <Form.Field
            fluid
            control={Select}
            options={versionOption}
            label="version"
            placeholder="Version"
            width={4}
          />
          <Form.Field
            control={Input}
            type="file"
            label="Project Files"
            directory=""
            webkitdirectory=""
          />
        </Form.Group>
        <Form.Field control={Checkbox} label="check to upload database" />
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            placeholder="Database"
            options={languageOption}
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            placeholder="Version"
            options={languageOption}
            width={4}
          />
        </Form.Group>

        <Form.Field
          control="input"
          label="Database backup file"
          type="file"
          accept=".sql"
        />
        <Form.Field
          control={TextArea}
          label="About"
          placeholder="Tell something about your project ..."
        />
        <Form.Field control={Checkbox} label="allow download" />
        <Button type="submit">Submit</Button>
      </React.Fragment>
    );
  }
}

export default WebsiteUploadForm;
