import React, { Component } from "react";
import {
  Checkbox,
  Button,
  Form,
  Select,
  Input,
  TextArea,
  Divider
} from "semantic-ui-react";

class WebsiteUploadForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedLanguage: null
    };
  }

  languageOnChange = (event, data) => {
    this.setState({ selectedLanguage: data.value });
  };

  render() {
    const detail = this.props.projectDetail;

    //extract website information
    const webDetail = detail.projects.filter(project => {
      return project.projectType === "Website";
    });
    //extract supported language from website information
    console.log(webDetail);
    const version = webDetail[0].language.filter(language => {
      return language.name === this.state.selectedLanguage;
    });

    console.log("version :", version);
    const language = webDetail[0].language.map(language => {
      return { text: language.name, value: language.name };
    });

    return (
      <React.Fragment>
        <Form.Field>
          <label> Upload Project Files</label>
          <input directory="" webkitdirectory="" type="file" />
        </Form.Field>
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            options={language}
            label="Language"
            placeholder="title"
            onChange={this.languageOnChange}
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            options={version}
            label="Version"
            placeholder="title"
            width={4}
          />
        </Form.Group>
        <Form.Field control={Checkbox} label="check to upload database" />
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            label="Database"
            placeholder="title"
            options={language}
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            label="Version"
            placeholder="title"
            options={language}
            width={4}
          />
        </Form.Group>
        <Form.Field
          control="input"
          label="Database backup file"
          type="file"
          accept=".sql"
        />
        <Form.Field
          control={TextArea}
          label="About"
          placeholder="Tell something about your project ..."
        />
        <Form.Field control={Checkbox} label="allow download" />
        <Button type="submit">Submit</Button>
      </React.Fragment>
    );
  }
}

export default WebsiteUploadForm;
