import React, { Component } from "react";
import WebsiteUploadForm from "./WebsiteUploadForm";
import WindowUploadForm from "./WindowUploadForm";
import AndroidUploadForm from "./AndroidUploadForm";
import axios from "axios";
import {
  TextArea,
  Checkbox,
  Button,
  Form,
  Select,
  Input
} from "semantic-ui-react";

class Upload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      projects: null,
      projectTypes: null,
      selectProjectType: "",
      selecteProjectName: null,
      selectLanguage: null,
      selectLanguageVersion: null,
      projectFiles: null,
      dbOnCheck: false,
      dbLanguage: null,
      dbVersion: null,
      dbBackUpFile: null,
      description: null,
      downloadPermission: false,
      androidDevice: null,
      apiVersion: null,
      androidVersion: null
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8080/supportLanguage")
      .then(response => {
        // handle success
        const pj = response.data;
        this.setState({
          loading: false,
          projects: pj,
          projectTypes: pj.projects.map(project => {
            return { text: project.projectType, value: project.projectType };
          }),
          selectProjectType: "Website"
        });
        console.log("data fetched", this.state.projectType);
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  }

  deviceOnChange = value => {
    this.setState({ androidDevice: value });
  };

  androidVersionOnChange = value => {
    this.setState({ androidVersion: value });
  };

  apiVersionOnChange = value => {
    this.setState({ apiVersion: value });
  };

  projectTypeOnChange = (event, data) => {
    this.setState({ selectProjectType: data.value });
    this.reset();
    console.log(data.value);
  };

  projectNameOnChange = (event, data) => {
    this.setState({ selectProjectName: event.target.value });
    console.log(this.state.selectProjectName);
  };

  languageOnChange = value => {
    this.setState({ selectLanguage: value });
  };

  languageVersionOnChange = value => {
    this.setState({ selectLanguageVersion: value });
  };

  projectFilesOnChange = value => {
    this.setState({ projectFiles: value });
  };

  dbOnCheck = value => {
    this.setState({ dbOnCheck: value });
  };

  dbOnChanage = value => {
    this.setState({ dbLanguage: value });
  };

  dbVersionOnChange = value => {
    this.setState({ dbVersion: value });
  };

  dbBackUpOnChange = value => {
    this.setState({ dbBackUpFile: value });
  };

  descriptionOnChange = (e, data) => {
    this.setState({ description: e.target.value });
  };

  downloadPermissionOnChange = (e, data) => {
    this.setState({ downloadPermission: data.checked });
  };

  onSubmit = e => {
    e.preventDefault();
    console.log("On Submit worked !");
    console.log(this.state);
  };

  validateForm = () => {
    let message = [];
    switch (this.state.selectProjectType) {
      case "Website":
        message +=
          this.state.selectLanguage == null
            ? message.push("Language required.")
            : "";
        message +=
          this.state.selectLanguageVersion == null
            ? message.push("Language version required.")
            : "";
        message +=
          this.state.projectFiles == null
            ? message.push("Project files required.")
            : "";

        if (this.state.dbOnCheck) {
          message +=
            this.state.dbLanguage == null
              ? message.push("Database required.")
              : "";
          message +=
            this.state.dbVersion == null
              ? message.push("Datbase version required.")
              : "";
          message +=
            this.state.dbBackUpFile == null
              ? message.push(".Sql backup required.")
              : "";
        }
        break;
      case "Window Application":
        message +=
          this.state.selectLanguage == null
            ? message.push("Language required.")
            : "";
        message +=
          this.state.selectLanguageVersion == null
            ? message.push("Language version required.")
            : "";
        message +=
          this.state.projectFiles == null
            ? message.push("Application file required.")
            : "";
        break;
      case "Android Appliation":
        message +=
          this.state.androidDevice == null
            ? message.push("Device required.")
            : "";
        message +=
          this.state.apiVersion == null
            ? message.push("API version required.")
            : "";
        message +=
          this.state.androidVersion == null
            ? message.push("Android version required.")
            : "";
        message +=
          this.state.projectFiles == null
            ? message.push("Application file required.")
            : "";
    }
    return message;
  };

  reset = () => {
    this.setState({
      selectLanguage: null,
      selectLanguageVersion: null,
      projectFiles: null,
      dbOnCheck: false,
      dbLanguage: null,
      dbVersion: null,
      dbBackUpFile: null,
      description: null,
      downloadPermission: false,
      androidDevice: null,
      apiVersion: null,
      androidVersion: null
    });
  };

  render() {
    const options = [
      { text: "Website", value: "website" },
      { text: "Window Application", value: "window application" },
      { text: "Android Application", value: "android application" }
    ];
    let uploadform;
    switch (this.state.selectProjectType) {
      case "Website":
        uploadform = (
          <WebsiteUploadForm
            projectDetail={this.state.projects}
            languageOnChange={this.languageOnChange}
            languageVersionOnChange={this.languageVersionOnChange}
            projectFilesOnChange={this.projectFilesOnChange}
            dbOnCheck={this.dbOnCheck}
            dbOnChange={this.dbOnChanage}
            dbVersionOnChange={this.dbVersionOnChange}
            dbBackUpOnChange={this.dbBackUpOnChange}
          />
        );
        break;
      case "Window Application":
        uploadform = (
          <WindowUploadForm
            projectDetail={this.state.projects}
            languageOnChange={this.languageOnChange}
            languageVersionOnChange={this.languageVersionOnChange}
            projectFilesOnChange={this.projectFilesOnChange}
          />
        );
        break;
      case "Android Application":
        uploadform = (
          <AndroidUploadForm
            projectDetail={this.state.projects}
            deviceOnChange={this.deviceOnChange}
            apiVersionOnChange={this.apiVersionOnChange}
            androidVersionOnChange={this.androidVersionOnChange}
            projectFilesOnChange={this.projectFilesOnChange}
          />
        );
        break;
    }

    return (
      <Form onSubmit={this.onSubmit}>
        <Form.Field
          fluid
          control={Select}
          label="Project Type"
          options={this.state.projectTypes}
          placeholder="Project Type"
          onChange={this.projectTypeOnChange}
        />
        <Form.Field
          inline
          fluid
          control={Input}
          label="Project Name"
          placeholder="Name"
          onChange={this.projectNameOnChange}
          required
        />
        {uploadform}
        <Form.Field
          control={TextArea}
          label="About"
          placeholder="Tell something about your project ..."
          onChange={this.descriptionOnChange}
          required
        />
        <Form.Field
          control={Checkbox}
          label="allow download"
          onChange={this.downloadPermissionOnChange}
        />
        <Button type="submit">Submit</Button>
      </Form>
    );
  }
}

export default Upload;
