import React, { Component } from "react";
import axios from "axios";
import {
  Checkbox,
  Button,
  Form,
  Select,
  Input,
  TextArea,
  Divider
} from "semantic-ui-react";

class Upload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      projects: null,
      projectType: null
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8080/supportLanguage")
      .then(response => {
        // handle success
        const pj = response.data;
        this.setState({
          loading: false,
          projects: pj,
          projectType: pj.projects.map(project => {
            return project.projectType;
          })
        });
        console.log("data fetched", this.state.projects);
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  }

  render() {
    const options = [
      { key: "w", text: "Website", value: "website" },
      { key: "d", text: "Window Application", value: "window application" },
      { key: "a", text: "Android Application", value: "android application" }
    ];

    return (
      <Form>
        <Form.Field
          fluid
          control={Select}
          label="Project Type"
          options={options}
          placeholder="Project Type"
          width={4}
        />
        <Form.Field
          inline
          fluid
          control={Input}
          label="Title"
          placeholder="title"
          width={4}
        />

        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            options={options}
            label="Language"
            placeholder="title"
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            options={options}
            label="Version"
            placeholder="title"
            width={4}
          />
        </Form.Group>
        <Form.Field control={Checkbox} label="check to upload database" />
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            label="Database"
            placeholder="title"
            options={options}
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            label="Version"
            placeholder="title"
            options={options}
            width={4}
          />
        </Form.Group>
        <Form.Field
          control="input"
          label="Database backup file"
          type="file"
          accept=".sql"
        />
        <Form.Field
          control={TextArea}
          label="About"
          placeholder="Tell us more about you..."
        />
        <Form.Field control={Checkbox} label="allow download" />
        <Button type="submit">Submit</Button>
      </Form>
    );
  }
}

export default Upload;
