import React, { Component } from "react";
import {
  Checkbox,
  Button,
  Form,
  Select,
  Input,
  TextArea,
  Divider
} from "semantic-ui-react";

class WindowUploadForm extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    const options = [
      { text: "Website", value: "website" },
      { text: "Window Application", value: "window application" },
      { text: "Android Application", value: "android application" }
    ];
    return (
      <React.Fragment>
        <Form.Field>
          <label> Upload Application </label>
          <input type="file" accept=".jar" />
        </Form.Field>
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            options={options}
            label="Language"
            placeholder="title"
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            options={options}
            label="Version"
            placeholder="title"
            width={4}
          />
        </Form.Group>
        <Form.Field control={Checkbox} label="check to upload database" />
        <Form.Group>
          <Form.Field
            fluid
            control={Select}
            label="Database"
            placeholder="title"
            options={options}
            width={4}
          />
          <Form.Field
            fluid
            control={Select}
            label="Version"
            placeholder="title"
            options={options}
            width={4}
          />
        </Form.Group>
        <Form.Field
          control="input"
          label="Database backup file"
          type="file"
          accept=".sql"
        />
        <Form.Field
          control={TextArea}
          label="Description"
          placeholder="Tell something about your project ..."
        />
        <Form.Field control={Checkbox} label="allow download" />
        <Button type="submit">Submit</Button>
      </React.Fragment>
    );
  }
}

export default WindowUploadForm;
