import React, { Component } from "react";
import WebsiteUploadForm from "./WebsiteUploadForm";
import WindowUploadForm from "./WindowUploadForm";
import AndroidUploadForm from "./AndroidUploadForm";
import axios from "axios";
import { Form, Select, Input } from "semantic-ui-react";

class Upload extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      projects: null,
      projectTypes: null,
      selectProjectType: ""
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8080/supportLanguage")
      .then(response => {
        // handle success
        const pj = response.data;
        this.setState({
          loading: false,
          projects: pj,
          projectTypes: pj.projects.map(project => {
            return { text: project.projectType, value: project.projectType };
          }),
          selectProjectType: "Website"
        });
        console.log("data fetched", this.state.projectType);
      })
      .catch(function(error) {
        // handle error
        console.log(error);
      });
  }

  projectTypeOnChange = (event, data) => {
    this.setState({ selectProjectType: data.value });
    console.log(data.value);
  };

  render() {
    const options = [
      { text: "Website", value: "website" },
      { text: "Window Application", value: "window application" },
      { text: "Android Application", value: "android application" }
    ];
    let uploadform;
    switch (this.state.selectProjectType) {
      case "Website":
        uploadform = <WebsiteUploadForm projectDetail={this.state.projects} />;
        break;
      case "Window Application":
        uploadform = <WindowUploadForm projectDetail={this.state.projects} />;
        break;
      case "Android Application":
        uploadform = <AndroidUploadForm projectDetail={this.state.projects} />;
        break;
    }

    return (
      <Form>
        <Form.Field
          fluid
          control={Select}
          label="Project Type"
          options={this.state.projectTypes}
          placeholder="Project Type"
          onChange={this.projectTypeOnChange}
        />
        <Form.Field
          inline
          fluid
          control={Input}
          label="Project Name"
          placeholder="Name"
        />
        {uploadform}
        <Form.Field
          control={TextArea}
          label="About"
          placeholder="Tell something about your project ..."
        />
        <Form.Field control={Checkbox} label="allow download" />
        <Button type="submit">Submit</Button>
      </Form>
    );
  }
}

export default Upload;
