import React, { Component } from "react";
import {
  Input,
  Select,
  Item,
  Button,
  Icon,
  Label,
  Form,
  Segment
} from "semantic-ui-react";
import axios from "axios";

class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pattern: "",
      projectType: "All",
      language: "All",
      languageOptions: [
        { text: "All", value: "All" },
        { text: "PHP", value: "PHP" },
        { text: "HTML", value: "HTML" },
        { text: "JAVA", value: "JAVA" }
      ]
    };
  }

  componentDidMount() {
    let data = {};
    if (this.state.pattern !== "" || this.state.pattern !== "All")
      data["pattern"] = this.state.pattern;
    if (this.state.language !== "" || this.state.language !== "ALL")
      data["language"] = this.state.language;
    if (this.state.projectType !== "" || this.state.projectType !== "ALL")
      data["projectType"] = this.state.projectType;

    axios({
      method: "get",
      url: "http://localhost:8080/getProject",
      data: data
    })
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  }

  projectTypeOnChange = (event, data) => {
    switch (data.value) {
      case "Website":
        this.setState({
          languageOptions: [
            { text: "All", value: "All" },
            { text: "PHP", value: "PHP" },
            { text: "HTML", value: "HTML" }
          ]
        });
        break;
      case "Window Application":
        this.setState({
          languageOptions: [
            { text: "All", value: "All" },
            { text: "JAVA", value: "JAVA" }
          ]
        });
        break;
      case "Android Appliation":
        this.setState({
          languageOptions: [{ text: "All", value: "All" }]
        });
        break;
      default:
    }
  };

  searchOnChange = e => {
    this.setState({ pattern: e.target.value });
    console.log(e.target.value);
  };

  searchOnClick = e => {
    console.log("clicked .");
  };
  render() {
    const paragraph = "hello world";
    const projectTypeOptions = [
      { text: "All", value: "All" },
      { text: "Website", value: "Website" },
      { text: "Window Application", value: "Window Application" },
      { text: "Android", value: "Android" }
    ];

    return (
      <React.Fragment>
        <div className="projectContent">
          <Form.Group>
            <Segment clearing>
              <Input placeholder="Search..." onChange={this.searchOnChange} />
              <Button content="Search" onClick={this.searchOnClick} />

              <span className="padd"></span>
              <Select
                placeholder="Project Type"
                options={projectTypeOptions}
                onChange={this.projectTypeOnChange}
              />
              <span className="padd"></span>
              <Select
                placeholder="Language"
                options={this.state.languageOptions}
              />
            </Segment>
          </Form.Group>
          <Segment>
            <Item.Group divided>
              <Item>
                <Item.Content>
                  <Item.Header as="a">12 Years a Slave</Item.Header>
                  <Item.Meta>
                    <span className="cinema">Union Square 14</span>
                  </Item.Meta>
                  <Item.Description>{paragraph}</Item.Description>
                  <Item.Extra>
                    <Label>IMAX</Label>
                    <Label icon="globe" content="Additional Languages" />
                  </Item.Extra>
                </Item.Content>
              </Item>

              <Item>
                <Item.Content>
                  <Item.Header as="a">My Neighbor Totoro</Item.Header>
                  <Item.Meta>
                    <span className="cinema">IFC Cinema</span>
                  </Item.Meta>
                  <Item.Description>{paragraph}</Item.Description>
                  <Item.Extra>
                    <Button primary floated="right">
                      Buy tickets
                      <Icon name="right chevron" />
                    </Button>
                    <Label>Limited</Label>
                  </Item.Extra>
                </Item.Content>
              </Item>

              <Item>
                <Item.Image src="/images/wireframe/image.png" />

                <Item.Content>
                  <Item.Header as="a">Watchmen</Item.Header>
                  <Item.Meta>
                    <span className="cinema">IFC</span>
                  </Item.Meta>
                  <Item.Description>{paragraph}</Item.Description>
                  <Item.Extra>
                    <Button primary floated="right">
                      Buy tickets
                      <Icon name="right chevron" />
                    </Button>
                  </Item.Extra>
                </Item.Content>
              </Item>
            </Item.Group>
          </Segment>
        </div>
      </React.Fragment>
    );
  }
}

export default Projects;
