import React, { Component } from "react";
import {
  Input,
  Select,
  Item,
  Button,
  Icon,
  Label,
  Form
} from "semantic-ui-react";

class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = {
      projectType: "All",
      language: "All",
      languageOptions: [
        { text: "All", value: "All" },
        { text: "PHP", value: "PHP" },
        { text: "HTML", value: "HTML" },
        { text: "JAVA", value: "JAVA" }
      ]
    };
  }

  componentDidMount() {}

  projectTypeOnChange = (event, data) => {
    switch (data.value) {
      case "Website":
        this.setState({
          languageOptions: [
            { text: "All", value: "All" },
            { text: "PHP", value: "PHP" },
            { text: "HTML", value: "HTML" }
          ]
        });
        break;
      case "Window Application":
        this.setState({
          languageOptions: [
            { text: "All", value: "All" },
            { text: "JAVA", value: "JAVA" }
          ]
        });
        break;
      case "Android Appliation":
        this.setState({
          languageOptions: [{ text: "All", value: "All" }]
        });
        break;
      default:
    }
  };

  render() {
    const paragraph = "hello world";
    const projectTypeOptions = [
      { text: "All", value: "All" },
      { text: "Website", value: "Website" },
      { text: "Window Application", value: "Window Application" },
      { text: "Android", value: "Android" }
    ];

    return (
      <React.Fragment>
        <div className="projectContent">
          <Form.Group>
            <Input action="Search" placeholder="Search..." />
            <Select
              placeholder="Project Type"
              options={projectTypeOptions}
              onChange={this.projectTypeOnChange}
            />
            <Select
              placeholder="Language"
              options={this.state.languageOptions}
            />
          </Form.Group>
          <Item.Group divided>
            <Item>
              <Item.Content>
                <Item.Header as="a">12 Years a Slave</Item.Header>
                <Item.Meta>
                  <span className="cinema">Union Square 14</span>
                </Item.Meta>
                <Item.Description>{paragraph}</Item.Description>
                <Item.Extra>
                  <Label>IMAX</Label>
                  <Label icon="globe" content="Additional Languages" />
                </Item.Extra>
              </Item.Content>
            </Item>

            <Item>
              <Item.Content>
                <Item.Header as="a">My Neighbor Totoro</Item.Header>
                <Item.Meta>
                  <span className="cinema">IFC Cinema</span>
                </Item.Meta>
                <Item.Description>{paragraph}</Item.Description>
                <Item.Extra>
                  <Button primary floated="right">
                    Buy tickets
                    <Icon name="right chevron" />
                  </Button>
                  <Label>Limited</Label>
                </Item.Extra>
              </Item.Content>
            </Item>

            <Item>
              <Item.Image src="/images/wireframe/image.png" />

              <Item.Content>
                <Item.Header as="a">Watchmen</Item.Header>
                <Item.Meta>
                  <span className="cinema">IFC</span>
                </Item.Meta>
                <Item.Description>{paragraph}</Item.Description>
                <Item.Extra>
                  <Button primary floated="right">
                    Buy tickets
                    <Icon name="right chevron" />
                  </Button>
                </Item.Extra>
              </Item.Content>
            </Item>
          </Item.Group>
        </div>
      </React.Fragment>
    );
  }
}

export default Projects;
